<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Dashboard extends CI_Controller {		/**	 * The Index Page.	 * The controller which shows options to register new patient.	 */	 	function  __construct() {		parent::__construct();		$this->load->model('clubs/dashboard_model');	}		public function index()	{		$data['active']='dashboard';		$heId = $this->session->userdata('HE_id');		$data['todayStatistics']=$this->dashboard_model->load_TodaysStatistics($heId);		$data['statistics']=$this->dashboard_model->load_statistics($heId);				if($this->session->userdata('club_logged')=='yes')		{						$this->load->view('clubs/dashboard',$data);		}		else{			header("location:".base_url().'clubs/login');		}	}		public function logout()	{		setcookie("user", "", time()-3600);		$this->session->unset_userdata('club_logged');		header("location:".base_url().'clubs/login');	}	}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */