<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Dashboard extends CI_Controller {	 	function  __construct() {		parent::__construct();		$this->load->model('dashboard_model');		if($this->session->userdata('admin_logged') ) {			if($this->session->userdata('admin_logged')=='yes') {							}			else {				header("location:".base_url().'login');			}		}		else {			header("location:".base_url().'login');		}	}		public function index()	{		$data['active']='dashboard';		$data['total_users']=$this->dashboard_model->total_users();		$data['total_posts']=$this->dashboard_model->total_posts();		$data['new_users']=$this->dashboard_model->new_users();		$data['new_posts']=$this->dashboard_model->new_posts();				$this->load->view('dashboard',$data);	}		public function logout()	{		$this->load->helper('cookie');		delete_cookie('remember_me');		$this->session->sess_destroy();		header("location:".base_url().'login');	}	}