<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Get_he extends CI_Controller {		/**	 * The Index Page.	 * The controller which shows options to register new patient.	 */	 	function  __construct() {		parent::__construct();		$this->load->model('he_model');	}		public function index()	{		$this->load->view('addhe');	}			public function get_he_details()	{		$type  = $this->input->get('type', TRUE);		$start  = $this->input->get('start', TRUE);		$limit  = $this->input->get('limit', TRUE);		$data = $this->he_model->get_HE_details($type,$start,$limit);		print_r($data);	}		public function get_he_details2()	{		$type = $this->input->post('type', TRUE);		$start = $this->input->post('start', TRUE);		$limit = $this->input->post('limit', TRUE);		$uid = $this->input->post('uid', TRUE);				if($uid==0) {			$heData = $this->he_model->get_HE_details($type,$start,$limit);			$total = $this->he_model->get_HE_details_Count($type);			$data = array( 				"heData"=> $heData,				"total" => $total			);			echo json_encode($data);			exit;		}				else {			$heData = $this->he_model->get_he_detailsForHI($type,$uid,$start,$limit);			$total = $this->he_model->get_HE_details_CountForHI($type,$uid);			$data = array( 				"heData"=> $heData,				"total" => $total			);			echo json_encode($data);			exit;		}	}		public function search_he()	{		$searchPattern  = $this->input->get('searchPattern', TRUE);		$data = $this->he_model->search_he($searchPattern);		print_r($data);	}		public function get_he_extra_details()	{		$id  = $this->input->get('id', TRUE);		$data = $this->he_model->get_HE_extra_details($id);		print_r($data);	}		}/* End of file welcome.php *//* Location: ./application/controllers/welcome.php */