<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Services extends CI_Controller {		/**	 * The Index Page.	 * The controller which shows options to Add new service.	 */	 	function  __construct() {		parent::__construct();		$this->load->model('he_model');	}		public function index()	{		$data['ser']=$this->he_model->servicelist_getall();		$this->load->view('service_list',$data);	}	public function add_service()	{		$this->load->view('add_service');	}		public function deleteEntry() {		$id = $this->input->post('id', TRUE);		$userExist =$this->he_model->delete_service($id);	}		public function add_new() {			$sercode  = $this->input->post('scode', TRUE); 			$sername   = $this->input->post('sname', TRUE);			$seriobs = $this->input->post('iobs', TRUE);			$date = date('Y-m-d H:i:s');			$curdate = $date;			$arr = array('serviceCode'=>($sercode),'serviceName' => ($sername),'MedSquareInternalObs'=>($seriobs),'insertionDate' => ($curdate),'Active' => 1);			$addserviceResult=$this->he_model->add_service($arr);						header('Location:'.base_url().'services/add_service');			exit;	}	}