<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Specializations extends CI_Controller {		/**	 * The Index Page.	 * The controller which shows options to Add new service.	 */	 	function  __construct() {		parent::__construct();		$this->load->model('he_model');	}		public function index()	{		$data['sep']=$this->he_model->specializationlist_getall();		$this->load->view('specialization_list',$data);	}	public function addSpecialization()	{		$this->load->view('add_specialization');	}		public function deleteEntry() {		$id = $this->input->post('id', TRUE);		$userExist =$this->he_model->delete_specialization($id);	}		public function add_newspecialization() {			$sercode  = $this->input->post('scode', TRUE); 			$sername   = $this->input->post('sname', TRUE);			$seriobs = $this->input->post('iobs', TRUE);			$date = date('Y-m-d H:i:s');			$curdate = $date;						$arr = array('specialCode'=>($sercode),'specialName' => ($sername),'MedSquareInternalObs'=>($seriobs),'insertionDate' => ($curdate),'Active' => 1);			$addspecializationResult=$this->he_model->add_specialization($arr);						header('Location:'.base_url().'specializations/addSpecialization');			exit;	}	}