<div class="span3 leftmenucolumn">  					<div class="accordion" id="leftMenu">						<div class="accordion-group">							<div class="accordion-heading">								<a class="accordion-toggle <?php if($active=='dashboard') echo "active"; ?>" data-parent="#leftMenu" href="<?php echo base_url();?>dashboard">									  <i class="icon-home"></i> Dashboard								</a>							</div>						</div>												<div class="accordion-group">							<div class="accordion-heading">								<a class="accordion-toggle dropable active" data-toggle="collapse" data-parent="#leftMenu" href="#collapseThree">									<i class="icon-edit"></i> HE records								</a>							</div>							<div id="collapseThree" class="accordion-body collapse" style="height: 0px; ">								<div class="accordion-inner">									<ul>										<li><a href="<?php echo base_url(); ?>clubs/he_companies/getInnerHESpecial" class="<?php if(isset($sub_active)) if($sub_active=='he_special') echo "active_sub_menu"; ?>">HE Specializations</a></li>										<li><a href="<?php echo base_url(); ?>clubs/he_companies/getInnerHEServices" class="<?php if(isset($sub_active)) if($sub_active=='he_services') echo "active_sub_menu"; ?>">HE Services</a> </li>										<li><a href="<?php echo base_url(); ?>clubs/hi_companies/getInnerHICompany" class="<?php if(isset($sub_active)) if($sub_active=='hi_companies') echo "active_sub_menu"; ?>">HE attended by HI Companies</a></li>										<li><a href="<?php echo base_url(); ?>clubs/hi_companies/getInnerHIPlan" class="<?php if(isset($sub_active)) if($sub_active=='hi_plans') echo "active_sub_menu"; ?>">HE attended by HI Plans</a></li>									</ul>                 								</div>							 </div>						</div>												</div>									</div>		<script>			$(document).ready(function() {							$('.active').find('i').addClass('icon-white');								$('.active').parent().parent().find('.accordion-body').css("height","auto");				$('.active').parent().parent().find('.accordion-body').addClass('in');								$('.accordion-toggle').click(function() {					if($(this).hasClass('active')) {						$(this).removeClass('active');						$('.accordion-toggle:not(.active) i').removeClass('icon-white');						$(this).find('i').addClass('icon-white');					}					else {						$('.active').removeClass('active');						$(this).addClass('active');						$('.accordion-toggle:not(.active) i').removeClass('icon-white');						$(this).find('i').addClass('icon-white');					}				});				$('.accordion-toggle').hover(function() {					$('.accordion-toggle:not(.active) i').removeClass('icon-white');					$(this).find('i').addClass('icon-white');				});				$('.accordion-toggle').mouseleave(function() {					if(!$(this).hasClass('active'))						$('.accordion-toggle:not(.active) i').removeClass('icon-white');				});							});		</script>