v>							<div id="collapse7" class="accordion-body collapse" style="height: 0px; ">								<div class="accordion-inner">									<ul>										<li>This is one</li>										<li>This is two</li>										<li>This is Three</li>									</ul>                 								</div>							 </div>						</div>												-->					</div>				</div>		<script>			$(document).ready(function() {							$('.active').find('i').addClass('icon-white');								$('.active').parent().parent().find('.accordion-body').css("height","auto");				$('.active').parent().parent().find('.accordion-body').addClass('in');								$('.accordion-toggle').click(function() {					if($(this).hasClass('active')) {						$(this).removeClass('active');						$('.accordion-toggle:not(.active) i').removeClass('icon-white');						$(this).find('i').addClass('icon-white');					}					else {						$('.active').removeClass('active');						$(this).addClass('active');						$('.accordion-toggle:not(.active) i').removeClass('icon-white');						$(this).find('i').addClass('icon-white');					}				});				$('.accordion-toggle').hover(function() {					$('.accordion-toggle:not(.active) i').removeClass('icon-white');					$(this).find('i').addClass('icon-white');				});				$('.accordion-toggle').mouseleave(function() {					if(!$(this).hasClass('active'))						$('.accordion-toggle:not(.active) i').removeClass('icon-white');				});							});		</script>