<!DOCTYPE html><html lang="en">	<head>  		<title>Yuplee login page</title>  		<meta name="viewport" content="width=device-width, initial-scale=1.0">		<meta name="description" content="MedSquare admin ">  		<meta name="author" content=""> 		<!-- Bootstrap -->  		<link href="<?php  echo base_url(); ?>css/bootstrap.min.css" rel="stylesheet" media="screen">		<link href="<?php  echo base_url(); ?>css/bootstrap-responsive.min.css" rel="stylesheet">		<link href="<?php  echo base_url(); ?>css/common.css" rel="stylesheet">		<link href="<?php  echo base_url(); ?>css/style_login.css" rel="stylesheet">		<script src="<?php  echo base_url(); ?>js/jquery-1.10.1.min.js"></script>  		<script src="<?php  echo base_url(); ?>js/bootstrap.min.js"></script>		<!--[if lt IE 9]>  		<script src="//html5shim.googlecode.com/svn/trunk/html5.js"></script>  		<![endif]-->	</head>	<body>		<div class="container">			<div class="row">				<div class="span3"></div>				<div class="span6">					<div class="row">						<div class="span6 logo">						</div>					</div>					<div class="row">						<div class="span6 loginbox">							<div class="row">								<div class="span1"></div>								<div class="span4 plslogin">Please login</div>								<div class="span1"></div>							</div>							<div class="row">								<div class="span1"></div>								<div class="span4 loginfields">									<div class="loginfield">										<input type="text" name="email" id="username" class="logininput" placeholder="email">										<span class="status"></span>									</div>									<div class="loginfield">										<span class="passwdleft"></span>										<input type="password" name="password" id="password" class="logininput" placeholder="password">										<span class="status"></span>									</div>									<div class="login_error"></div>								</div>								<div class="span1"></div>							</div>							<div class="row">								<div class="span1"></div>								<div class="span4 rememberme">									<input type="checkbox" id="remembercheck" name="cc" />									<label for="remembercheck"><span></span>Remember me .</label> 									<a href="javascript:void(0);" id="forgotpasswd"> Forgot password? </a>								</div>								<div class="span1"></div>							</div>							<div class="row">								<div class="span2"></div>								<div class="span2 loginbutton" id="loginbutton">Login</div>								<div class="span2 loading hide"></div>								<div class="span2"></div>							</div>						<!--	<div class="row">								<div class="span1"></div>								<div class="span4 donthave">									Don't have an account? <a href=""> Create one new </a>								</div>								<div class="span1"></div>							</div> -->						</div>						<div class="span6 forgotbox hide">							<div class="row">								<div class="span1"></div>								<div class="span4 plslogin">Forgot Password</div>								<div class="span1"></div>							</div>							<div class="row">								<div class="span1"></div>								<div class="span4 loginfields">									<div class="loginfield">										<input type="text" name="username" id="forgotusername" class="logininput" placeholder="email">										<span class="status"></span>									</div>								</div>								<div class="span1"></div>							</div>							<div class="row">								<div class="span1"></div>								<div class="span4 forgotbutton" id="forgotbutton">Recover my password</div>								<div class="span4 loading hide"></div>								<div class="span1"></div>							</div>						</div>						<div class="sidenavs">							<div class="sidenav sideactive" id="loginnav"></div>							<div class="sidenav" id="addusernav"></div>							<div class="sidenav" id="forgetnav"></div>						</div>					</div>				</div>				<div class="span3"></div>			</div>		</div>		<footer>			<div class="container">				<div class="row">					<div class="span12 headerspan">						<div class="copyright">&copy; 2013, Yuplee. All rights reserved. </div>						<div class="createdby">Created by: Zeromaze</div>					</div>				</div>			</div>		</footer>				<script>			var usernameValid=0;			var passwordValid=0;			var emailValid=0;						$(document).ready(function() {							$('#forgotpasswd').click(function() {					$('.loginbox').fadeOut(300,'linear',function() {						$('.forgotbox').fadeIn(300);					});					$('.sideactive').removeClass('sideactive');					$('#forgetnav').addClass('sideactive');				});				$('#forgetnav').click(function() {					$('.loginbox').fadeOut(300,'linear',function() {						$('.forgotbox').fadeIn(300);					});					$('.sideactive').removeClass('sideactive');					$('#forgetnav').addClass('sideactive');				});								$('#loginnav').click(function() {					$('.forgotbox').fadeOut(300,'linear',function() {						$('.loginbox').fadeIn(300);					});					$('.sideactive').removeClass('sideactive');					$('#loginnav').addClass('sideactive');				});								$('#forgotusername').keyup(function() {					$('#forgotusername').next().show();					var email=$('#forgotusername').val();					if(!validateEmail(email)) {						$('#forgotusername').next().addClass('error');						emailValid=1;					}					else {						$('#forgotusername').next().removeClass('error');						emailValid=0;					}				});				$('#password').keyup(function() {					$('#password').next().show();					var password=$('#password').val();					if(password.length < 5) {						$('#password').next().addClass('error');						passwordValid=1;					}					else {						$('#password').next().removeClass('error');						passwordValid=0;					}				});				$('#username').keyup(function() {					$('#username').next().show();					var email=$('#username').val();					if(!validateEmail(email)) {						$('#username').next().addClass('error');						usernameValid=1;					}					else {						$('#username').next().removeClass('error');						usernameValid=0;					}				});								$('#loginbutton').click(function() {					if(passwordValid==1 || usernameValid==1){						$('#password').next().show();						var password=$('#password').val();						if(password.length < 5) {							$('#password').next().addClass('error');							passwordValid=1;							$('.login_error').html('Your password must be at least 5 characters long.');						}						else {							$('#password').next().removeClass('error');							passwordValid=0;						}						$('#username').next().show();						var email=$('#username').val();						if(!validateEmail(email)) {							$('#username').next().addClass('error');							usernameValid=1;							$('.login_error').html('Please enter a valid email.');						}						else {							$('#username').next().removeClass('error');							usernameValid=0;							if(passwordValid==0)								login();						}					}					else {						login();					}				});								$('#forgotbutton').click(function() {					if(emailValid==1){						$('#forgotusername').next().show();						var email=$('#forgotusername').val();						if(!validateEmail(email)) {							$('#forgotusername').next().addClass('error');							emailValid=1;						}						else {							$('#forgotusername').next().removeClass('error');							emailValid=0;							forgotpassword();						}					}					else {						forgotpassword();						//alert('Submitted');											}				});			});						function validateEmail(email) { 				var re =/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;				return re.test(email);			}			function login() { 						$('#loginbutton').hide();						$('.loading').show();												var datastring = 'email='+$('#username').val()+'&password='+$('#password').val()+'&remember='+$('#remembercheck').is(':checked');						console.log(datastring);						$.ajax({								type: "POST",    								url: '<?php echo base_url(); ?>login/check_login',								data: datastring,								cache: false,								success: function(msg)								{									console.log(msg);									if(msg=='success') {										window.location.href='<?php echo base_url(); ?>dashboard';									}									else {										$('#loginbutton').show();										$('.loading').hide();										$('.login_error').html(msg);									}								},								error: function(xhr, status, error) {									$('.login_error').html(error);								}							});			}			function forgotpassword() { 						$('#forgotbutton').hide();						$('.loading').show();												var datastring = 'email='+$('#forgotusername').val();						console.log(datastring);						$.ajax({								type: "POST",    								url: '<?php echo base_url(); ?>login/check_forgot_login',								data: datastring,								cache: false,								success: function(msg)								{									console.log(msg);									if(msg=='success') {										$('#forgotbutton').show();										$('.loading').hide();										alert("Please check your email for new password.");									}									else {										$('#forgotbutton').show();										$('.loading').hide();										$('.login_error').html(msg);									}								},								error: function(xhr, status, error) {									$('.login_error').html(error);								}							});			}		</script>	</body></html>