										$userstate= 'Not verified';										else 											$userstate= 'Verified';																				echo '<tr>';										echo '<td style="text-align:center"><a href="'.base_url().'user_records/userinfo?users_id='.$user->users_id.'" class="userinfolink"></a></td>';										echo '<td>'.$user->username.'</td>';										echo '<td style="width:155px;">'.$user->email.'</td>';										echo '<td>'.$user->user_type.'</td>';										echo '<td>'.$user->phone_number.'</td>';										echo '<td><div class="status '.$userstate.'">'.$userstate.'</div></td>';										echo '<td>'.$user->state_id.'</td>';										echo '<td>'.$user->city_id.'</td>';										echo '<td>'.$user->date.'</td>';										echo '<td style="text-align:center">											<input type="button" class="deletebutton" onClick="delete_user(this,'.$user->users_id.',\''.$user->username.'\')">											</td>';										echo '</tr>';									}								?>							</tbody>  						  </table>						</div>					</div>					<div class="row leftalign">						<div class="span8 span8bigger paginationcontainer">							<?php echo $links; ?>						</div>					</div>				</div>			</div>		</div>		<?php include "include/footer.php"; ?>		<script>			function delete_user(button,id,name) {						var base_url = '<?php echo base_url(); ?>';						var datastring = "&id="+id;						var conf = confirm("Are you sure you want to delete "+name+" ?");												if(conf == true){							$.ajax({								url: base_url + 'user_records/delete_user',								data: datastring,								type: 'post',								success: function(data) {									console.log(data);									if(data==1)										$(button).parent().parent().remove();									else										$('#'+id).val('Set Active');								}							});						}			}		</script>	</body></html>